import React, { useState, useEffect, useCallback, useMemo, useRef } from 'react';
import './App.css';
import { 
  ANIMATION_DURATION, 
  UPDATE_INTERVAL, 
  CLOCK_UPDATE_INTERVAL, 
  ANIMATION_STEPS,
  TARGET_VALUES,
  GRAPH_DATA,
  BAR_DATA,
  MENU_ITEMS,
  WEEK_OPTIONS,
  GRADE_OPTIONS,
  MONTH_NAMES,
  WEEKDAY_NAMES,
  STUDENT_DATA
} from './constants';
import { 
  formatTime, 
  formatMonthYear, 
  getSeoulTime, 
  getCalendarData,
  validateGraphData,
  validateBarData,
  getRandomInRange,
  formatNumber
} from './utils';

function App() {
  // State for interactive features
  const [activeMenu, setActiveMenu] = useState('대시보드');
  const [selectedWeek, setSelectedWeek] = useState('1주차');
  const [selectedGrade, setSelectedGrade] = useState('Grade 3');
  const [currentDate, setCurrentDate] = useState(new Date());
  const [selectedDate, setSelectedDate] = useState(22); // 22일이 선택된 상태
  const [selectedMonth, setSelectedMonth] = useState('9월'); // 선택된 월
  const [hoveredPoint, setHoveredPoint] = useState(null); // 호버된 데이터 포인트
  const [isAnimating, setIsAnimating] = useState(false); // 애니메이션 상태
  const [liveData, setLiveData] = useState({
    onlineUsers: 0,
    newMessages: 0,
    currentTime: getSeoulTime(),
    isLive: true
  });
  const [animatedValues, setAnimatedValues] = useState({
    learners: 0,
    achievement: 0,
    progress: 0,
    utilization: 0,
    attendance: 0,
    usageTime: 0
  });

  // Animation effect for numbers
  useEffect(() => {
    const stepDuration = ANIMATION_DURATION / ANIMATION_STEPS;
    let currentStep = 0;
    let timer = null;

    try {
      timer = setInterval(() => {
      currentStep++;
        const progress = currentStep / ANIMATION_STEPS;

      setAnimatedValues({
          learners: Math.floor(TARGET_VALUES.learners * progress),
          achievement: Math.floor(TARGET_VALUES.achievement * progress),
          progress: Math.floor(TARGET_VALUES.progress * progress),
          utilization: Math.floor(TARGET_VALUES.utilization * progress),
          attendance: Math.floor(TARGET_VALUES.attendance * progress),
          usageTime: Math.floor(TARGET_VALUES.usageTime * progress)
        });

        if (currentStep >= ANIMATION_STEPS) {
        clearInterval(timer);
          setAnimatedValues(TARGET_VALUES);
      }
    }, stepDuration);
    } catch (error) {
      console.warn('Animation error:', error);
      setAnimatedValues(TARGET_VALUES);
    }

    return () => {
      if (timer) {
        clearInterval(timer);
      }
    };
  }, []);

  // Real-time clock update
  useEffect(() => {
    let clockInterval = null;

    try {
      clockInterval = setInterval(() => {
      setLiveData(prev => ({
        ...prev,
          currentTime: getSeoulTime()
        }));
      }, CLOCK_UPDATE_INTERVAL);
    } catch (error) {
      console.warn('Clock update error:', error);
    }

    return () => {
      if (clockInterval) {
        clearInterval(clockInterval);
      }
    };
  }, []);

  // Live data simulation
  useEffect(() => {
    let liveInterval = null;

    try {
      liveInterval = setInterval(() => {
      setLiveData(prev => ({
        ...prev,
          onlineUsers: getRandomInRange(120, 170),
          newMessages: getRandomInRange(0, 3)
      }));

        // Random metric updates with bounds checking
      setAnimatedValues(prev => ({
        ...prev,
          learners: Math.max(0, prev.learners + getRandomInRange(-1, 2)),
          achievement: Math.max(85, Math.min(98, prev.achievement + getRandomInRange(-1, 2))),
          progress: Math.max(0, prev.progress + getRandomInRange(-5, 10)),
          utilization: Math.max(110, Math.min(140, prev.utilization + getRandomInRange(-1, 2))),
          attendance: Math.max(75, Math.min(95, prev.attendance + getRandomInRange(-1, 2))),
          usageTime: Math.max(75, Math.min(95, prev.usageTime + getRandomInRange(-1, 2)))
        }));
      }, UPDATE_INTERVAL);
    } catch (error) {
      console.warn('Live data update error:', error);
    }

    return () => {
      if (liveInterval) {
        clearInterval(liveInterval);
      }
    };
  }, []);

  // 메뉴 클릭 핸들러 - 지연된 상태 업데이트
  const handleMenuClick = (menuItem) => {
    console.log('Menu clicked:', menuItem);
    setTimeout(() => {
    setActiveMenu(menuItem);
    }, 0);
  };

  // Calendar functions
  const navigateMonth = useCallback((direction) => {
    setCurrentDate(prevDate => {
      try {
      const newDate = new Date(prevDate);
      newDate.setMonth(prevDate.getMonth() + direction);
      return newDate;
      } catch (error) {
        console.warn('Date navigation error:', error);
        return prevDate;
      }
    });
  }, []);

  const handleDateClick = useCallback((date) => {
    if (typeof date === 'number' && date >= 1 && date <= 31) {
    setSelectedDate(date);
    }
  }, []);

  const handleMonthClick = useCallback((month) => {
    if (typeof month === 'string' && MONTH_NAMES.includes(month)) {
    setSelectedMonth(month);
    setIsAnimating(true);
    setTimeout(() => setIsAnimating(false), 1000);
    }
  }, []);

  // Memoized graph data with validation
  const graphData = useMemo(() => {
    return validateGraphData(GRAPH_DATA) ? GRAPH_DATA : [];
  }, []);

  const handlePointHover = useCallback((index) => {
    if (typeof index === 'number' && index >= 0 && index < graphData.length) {
    setHoveredPoint(index);
    }
  }, [graphData.length]);

  const handlePointLeave = useCallback(() => {
    setHoveredPoint(null);
  }, []);

  // Memoized calendar data
  const calendarData = useMemo(() => {
    return getCalendarData(currentDate);
  }, [currentDate]);

  // Memoized bar chart data with validation
  const barData = useMemo(() => {
    return validateBarData(BAR_DATA) ? BAR_DATA : [];
  }, []);

  // Student page component
  const StudentPage = () => (
    <div className="main-content">
      {/* Header */}
      <div className="header">
          <div className="search-container">
            <input 
              type="text" 
              placeholder="Q Search" 
              className="search-input"
              aria-label="검색"
              onKeyDown={(e) => {
                if (e.key === 'Enter') {
                  e.preventDefault();
                }
              }}
            />
          </div>

        <div className="header-right">
          <div className="notification-icons">
            <div className="notification-icon">🔔</div>
            <div className="notification-icon">⚙️</div>
          </div>
          <div className="profile-picture">옥</div>
          <div className="professor-name">옥지원 교수</div>
        </div>
      </div>

      {/* Student Profile Card */}
      <div className="student-profile-section">
        <div className="student-profile-card">
          <div className="student-avatar">
            <div className="avatar-placeholder">👨‍🎓</div>
          </div>
          <div className="student-info">
            <div className="student-name">{STUDENT_DATA.profile.name}</div>
            <div className="student-details">
              <div className="student-id">학번: {STUDENT_DATA.profile.studentId}</div>
              <div className="student-grade">{STUDENT_DATA.profile.grade}</div>
              <div className="student-contact">{STUDENT_DATA.profile.contact}</div>
            </div>
          </div>
        </div>

        {/* Key Metrics Cards */}
        <div className="student-metrics-grid">
          <div className="student-metric-card">
            <div className="metric-icon">👥</div>
            <div className="metric-content">
              <div className="metric-value">{STUDENT_DATA.metrics.attendance}</div>
              <div className="metric-label">출결 현황</div>
            </div>
          </div>
          <div className="student-metric-card">
            <div className="metric-icon">📅</div>
            <div className="metric-content">
